import React, { Component } from 'react';
import {Popover, OverlayTrigger, Button} from 'react-bootstrap';
import './User.css'

export default class User extends Component {
    constructor(props) {
        super(props);
        this.state = {
            likedSongs: this.props.likedSongs
        }
    }

    componentDidUpdate(prevProps) {
        // Typical usage (don't forget to compare props):
        if (this.props.likedSongs !== prevProps.id) {
          this.setState({ lead_id: this.props.id });
        }
        if (this.props.foreclose !== prevProps.foreclose) {
          this.setState({ isForeclosed: this.props.foreclose });
        }
    }
    render() {
        const popover = (
            <Popover>
              <Popover.Title as="h3">what are u listening to?</Popover.Title>
              <Popover.Content>
                <ul>
                    {this.state.likedSongs.map(song => {
                        return <li>hi</li>
                    })}
                </ul>
              </Popover.Content>
            </Popover>
          );

        return (
            <div className='User'>
                <OverlayTrigger trigger="click" placement="right" overlay={popover}>
                    <img src={this.props.userImage}/>
                </OverlayTrigger>
            </div>
        )
    }
}
