import React, { Component } from 'react'
import Spotify from 'spotify-web-api-js';
import TrackList from './TrackList';
import './SearchBar.css'

const spotifyWebApi = new Spotify();

export default class SearchBar extends Component {
    constructor(props) {
        super(props);
        this.state = {
            searchParams: '',
            searchResults: []
        }
        this.handleChange = this.handleChange.bind(this);
        // this.handleSubmit = this.handleSubmit.bind(this);
    }

    // handleSubmit(e) {
    //     e.preventDefault();

    //     // search tracks on spotify, update searchResults
    //     const searchParams = this.state.searchParams;
    //     spotifyWebApi.searchTracks(searchParams)
    //       .then((data) => {
    //         let results = [];
    //         data.tracks.items.forEach(item => {results.push(item)});
    //         this.setState({searchResults: results});
    //       }, (err) => {
    //         console.log(err);
    //       })
    // }

    handleChange(e) {
    // console.log(e.target.value);
    this.setState({searchParams: e.target.value});
    }

    spotifySearch(str) {
        spotifyWebApi.searchTracks(str)
          .then((data) => {
            let results = [];
            data.tracks.items.forEach(item => {results.push(item)});
            this.setState({searchResults: results});
            // return results;
          }, (err) => {
            console.log(err);
          })
    }


    render() {

        if (this.state.searchParams.length > 0) {
            this.spotifySearch(this.state.searchParams);
        } 

        let results = [];
        if (this.state.searchResults.length > 0) {
            results = this.state.searchResults;
        } else {
            results = [];
        }

        return (
            <div>
                <input type='text' onChange={this.handleChange} value={this.state.searchParams} placeholder='Search for songs on Spotift'/>
                <ul className='searchResults'>
                    {results.map(result => {
                        console.log(result)
                        return <li id='result' key={result.id}>{result.name} by {result.artists[0].name}</li>
                    })}
                </ul>


            </div>


        )
    }
}
