import React, { Component } from 'react'
import './NowPlaying.css'
import {Card} from 'react-bootstrap';

export default class NowPlaying extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isLoaded: false,
            playbackState: {
                item: {
                    id: null
                }
            }
        }
    }


    componentWillMount(prevProps) {
        this.props.getPlayback()
            .then(res => {
                this.setState({playbackState: res});
            }
        )
        // if (this.props.playbackState.hasOwnProperty('item')) {
        //     console.log('hi')
        //     this.setState({isLoaded: true});
        // }
    }

    render() {
        return (
            <div className='NowPlaying text-center'>
                {/* <Card>
                    <Card.Img variant='top' src=''/>
                </Card> */}
                <div style={{fontFamily: 'Roboto Slab, serif'}}>#NP:</div>
                <div style={{fontFamily: 'Roboto Slab, serif'}}>
                    {this.state.isLoaded ? this.state.playbackState.item.name : 'Loading'}
                </div>
            </div>
        )
    }
}
