import React, { Component } from 'react';
import {Popover, OverlayTrigger} from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faUser, faPlayCircle } from '@fortawesome/free-solid-svg-icons';
import './User.css';



export default class User extends Component {
    constructor(props) {
        super(props);
        this.state = {
            likedSongs: this.props.likedSongs,
            isPlaying: false
        }
        this.handlePlay = this.handlePlay.bind(this);
        this.handlePause = this.handlePause.bind(this);
    }

    componentDidUpdate(prevProps) {
        // Typical usage (don't forget to compare props):
        if (this.props.likedSongs !== prevProps.likedSongs) {
          this.setState({ likedSongs: this.props.likedSongs });
        }
    }

    handlePlay(song) {
        this.props.playSong(song);
        this.setState({isPlaying: true});
    }

    handlePause() {
        this.props.getPlayback().then(res => {
            this.setState({isPlaying: res.is_playing});
        })

        if (!this.state.isPlaying) {
            return;
        } else {
            this.props.pauseSong();
        }
    }

    render() {

        // overlay will show user's liked songs
        const popover = (
            <Popover className='User-popover'>
              <Popover.Title as="h3">what are u listening to?</Popover.Title>
                {this.state.likedSongs.map((song, i) => {
                    return  <Popover.Content className='User-popover-content'>
                                <FontAwesomeIcon className='mr-2' onClick={() => this.handlePlay(song)} icon={faPlayCircle} size='2x'/>
                                <a>{song.name}</a>
                            </Popover.Content>
                })}
            </Popover>
          );

        return (
            <div className='User'>
                {
                    !this.props.userImage ? 
                        <FontAwesomeIcon icon={faUser} size='10x'/> 
                    : 
                    <OverlayTrigger trigger="click" placement="right" overlay={popover}>
                        <img src={this.props.userImage} onClick={this.handlePause}/>
                    </OverlayTrigger>
                }
            </div>
        )
    }
}
