import React, { Component } from 'react'
import './NowPlaying.css'
import {Card} from 'react-bootstrap';

export default class NowPlaying extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isLoaded: true,
        }
    }

    componentDidUpdate(prevProps) {
        if (prevProps.currentSong && prevProps.currentSong.item.id != this.props.currentSong.item.id) {
            this.setState({current: this.props.currentSong})
        }
        console.log(this.props.currentSong);
    }


    render() {
        let songName = () => {
            if (this.props.currentSong.hasOwnProperty('item')) {
                return this.props.currentSong.item.name
            } else {
                return 'loading'
            }
        }
        return (
            <div className='NowPlaying text-center'>
                {/* <Card>
                    <Card.Img variant='top' src=''/>
                </Card> */}
                <div style={{fontFamily: 'Roboto Slab, serif'}}>#NP:</div>
                <div style={{fontFamily: 'Roboto Slab, serif'}}>
                    {/* {this.props.currentSong.hasOwnProperty('item') ? this.props.currentSong.item.name : 'Loading'} */}
                    {songName()}
                </div>
            </div>
        )
    }
}
