import React, { Component } from 'react'
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
} from "react-router-dom";
import AuthApi from '../AuthApi';

export default class SignIn extends Component {
    constructor(props) {
        super(props);
        this.state = {
            auth: false
        }
    }

    render() {
        return (
            <div>
                <AuthApi.Provider>
                    <Router>
                        <Routes/>
                    </Router>

                </AuthApi.Provider>

            </div>
        )
    }
}

const Login = () => {
    return (
        <div>
            <button>Login</button>
        </div>
    )
}

const Dashboard = () => {
    return (
        <div>
            <h1>Dashboard</h1>
            <button>Logout</button>
        </div>
    )
}

const Routes = () => {
    return (
        <Switch>
            <Route to path="/login" component={Login}/>
            <ProtectedRoute to path="/dashboard" component={Dashboard}/>
        </Switch>
    )
}

const ProtectedRoute = ({component:Component, ...rest}) => {
    return(
        <Route
            {...rest}
            render = {() => (
                <Component/>
            )}
        />
    )
}
