import React, { Component } from 'react'
import './NowPlaying.css'
import {Card} from 'react-bootstrap';

export default class NowPlaying extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isLoaded: true,
            playbackState: this.props.getCurrentlyPlaying()
        }
    }

    componentDidUpdate(prevProps) {

    }


    render() {
        return (
            <div className='NowPlaying text-center'>
                {/* <Card>
                    <Card.Img variant='top' src=''/>
                </Card> */}
                <div style={{fontFamily: 'Roboto Slab, serif'}}>#NP:</div>
                <div style={{fontFamily: 'Roboto Slab, serif'}}>
                    {this.state.isLoaded && this.state.playbackState.hasOwnProperty('item') ? this.state.playbackState.item.name : 'Loading'}
                </div>
            </div>
        )
    }
}
