import React, {Component} from 'react';
import Spotify from 'spotify-web-api-js';
import {Button} from 'react-bootstrap';
import './App.css';
import SearchBar from './SearchBar';
import NavBar from './NavBar';
import User from './User';

const spotifyWebApi = new Spotify();


class App extends Component{

  constructor(props) {
    super(props);

    // Gets access token
    const params = this.getHashParams();
    if (params.access_token) {
      spotifyWebApi.setAccessToken(params.access_token);
    }

    this.state = {
      loggedIn: params.access_token===spotifyWebApi.getAccessToken() ? true : false,
      username: '',
      nowPlaying: {
        name: 'Not checked',
        image: ''
      },
      userImage:'',
      searchParams: '',
      likedSongs: [],
      access_token: spotifyWebApi.getAccessToken() || null
    }

    this.getNowPlaying = this.getNowPlaying.bind(this);
    this.updateLikes = this.updateLikes.bind(this);
    this.playSong = this.playSong.bind(this);
  }


  getHashParams() {
    var hashParams = {};
    var e, r = /([^&;=]+)=?([^&;]*)/g,
        q = window.location.hash.substring(1);
    while ( e = r.exec(q)) {
       hashParams[e[1]] = decodeURIComponent(e[2]);
    }
    return hashParams;
  }

  componentDidMount() {

    // console.log(spotifyWebApi.getAccessToken());

    // fetch('https://api.spotify.com/v1/me', {
    //   headers: {
    //     'Authorization': 'Bearer ' + spotifyWebApi.getAccessToken()
    //   }
    // }).then((response) => response.json())
    // .then(data => {console.log(data)});

    spotifyWebApi.getMe()
      .then(res => {
          this.setState(
            {
              username: res.display_name,
              userImage: res.images[0].url
            }
          );
          console.log(res);
      });
  }

  getNowPlaying() {
    spotifyWebApi.getMyCurrentPlaybackState()
      .then(response => {
        let song = response ? {name: response.item.name, image: response.item.album.images[0].url} : {name: 'unidentified', image : 'unidentified'}
        this.setState({nowPlaying: song});
      })
  }

  getUser() {
    spotifyWebApi.getMe()
      .then(response => {
        this.setState({
          username: response.display_name
        })
      });
  }

  updateLikes(likedSong) {
    let likedSongs = this.state.likedSongs;
    if (likedSongs.length === 3) {
      likedSongs.shift();
      likedSongs.push(likedSong)
    } else {
      likedSongs.push(likedSong);
    }
    this.setState({
      likedSongs: [...likedSongs]
    });
  }

  async playSong() {
    let songs = {
      "device_id": '',
      "context_uri": "spotify:track:2E5QeM5h5OjfmCwuvDmNLY"
    }
    await spotifyWebApi.play(songs);
    // fetch('https://api.spotify.com/v1/me/player/play', {
    //   headers: {
    //     'Authorization': 'Bearer ' + spotifyWebApi.getAccessToken()
    //   }
    // }).then((response) => response.json())
    // .then(data => {console.log(data)});
  }


  render() {
    return (
      <div className="App">
        <NavBar loggedIn={this.state.loggedIn} username={this.state.username}/> 

        <SearchBar token={spotifyWebApi.getAccessToken()} updateLikes={this.updateLikes} playSong={this.playSong}/>
        <Button onClick={this.playSong}>Play song</Button>

        {/* <div>Now Playing: {this.state.nowPlaying.name}</div>
        <div>
          <img src={this.state.nowPlaying.image} style={{width: 100}}/>
        </div>
        <button onClick={this.getNowPlaying}>
          Check Now Playing
        </button> */}
        <User userImage={this.state.userImage} likedSongs={this.state.likedSongs}/>


      </div>
    );
  }
}

export default App;
